import React from "react";

export const ContactPicker = ({ contacts, onChange }) => {
  return (
    <select onChange={onChange}>
      <option value="">Select a contact</option>
      {contacts.map((contact) => (
        <option key={contact} value={contact}>
          {contact}
        </option>
      ))}
    </select>
  );
};
